import React, { useState, memo, useCallback, useEffect } from "react";

const TextInput = memo(({ value, onChange, name, type, className }) => {
  return <input type={type} name={name} value={value} required onChange={onChange} className={className} />;
});

const useInput = (initValue = null) => {
  const [value, setter] = useState(initValue);
  const handler = useCallback((e) => {
    setter(e.target.value);
  }, []);
  return [value, handler];
};

const AuthForm = () => {
  const [email, onChangeEmail] = useInput("");
  const [userName, onChangeUserName] = useInput("");
  const [authError, setAuthError] = useState("");
  const [phone, onChangetPhone] = useInput("");
  const [curriculum, setCurriculum] = useState("");

  useEffect(() => {
    console.log(curriculum);
    if (curriculum === "") {
      setAuthError("");
    }
  }, [curriculum]);

  const onSubmit = async (event) => {
    event.preventDefault();
    console.log(curriculum);
  };

  const onChangeCurriculum = (e) => {
    setCurriculum(e.target.value);
  };

  return (
    <div className="authContainer">
      <h3>한국경영원 인재개발원</h3>
      <form onSubmit={onSubmit} className="container">
        <select name="curriculum" className="authInput" value={curriculum} onChange={onChangeCurriculum} data-size="2">
          <option value="NONE">훈련직종선택</option>
          <option value="SpringMVC">SpringMVC</option>
          <option value="Java_Python">자바&파이썬</option>
        </select>
        {authError && <span className="authError">{authError}</span>}
        <input name="email" type="email" placeholder="Email" value={email} onChange={onChangeEmail} className="authInput" />
        <input name="userName" type="text" placeholder="이름" value={userName} onChange={onChangeUserName} className="authInput" />
        <input name="phone" type="phone" placeholder="전화번호" value={phone} onChange={onChangetPhone} className="authInput" />
        <input type="submit" className="authInput authSubmit" value="등록하기" />
      </form>
    </div>
  );
};
export default AuthForm;
